document.addEventListener("DOMContentLoaded",()=>{window.addEventListener("scroll",()=>{const e=document.getElementById("mainHeader").clientHeight;window.scrollY>105?(document.getElementById("mainHeader").classList.add("main-header-fixed"),document.getElementById("mainRow").setAttribute("style",`padding-top: ${e}px;`)):(document.getElementById("mainHeader").classList.remove("main-header-fixed"),document.getElementById("mainRow").setAttribute("style","padding-top: 0px;"))}),window.addEventListener("resize",()=>{document.getElementById("mainHeader").clientWidth>768&&(document.getElementById("mobileMenu").classList.remove("mobile-menu-container-open"),document.getElementById("hamburger").classList.remove("open"))})});document.getElementById("hamburger").addEventListener("click",e=>{e.preventDefault(),document.getElementById("hamburger").classList.toggle("open"),document.getElementById("mobileMenu").classList.toggle("mobile-menu-container-open")});document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".archive-filter a"),d=document.querySelector(".archive-grid");!d||e.length===0||e.forEach(n=>{n.addEventListener("click",r=>{r.preventDefault();const i=n.getAttribute("data-filter");e.forEach(t=>t.classList.remove("active")),n.classList.add("active"),Array.from(d.children).forEach(t=>{i==="all"||t.classList.contains(i)?t.classList.remove("hidden"):t.classList.add("hidden")})})})});
